Text serialization code:

private void writeObject(ObjectOutputStream out) throws IOException      {
       out.defaultWriteObject();
       if (textShape != null) {
           out.writeBoolean(true);
           // write general path
                    PathIterator it = textShape.getPathIterator(null);
           // count it (for easier reading)
           int count = 0;
           while (!it.isDone()) {
               count++;
               it.next();
           }
           out.writeInt(it.getWindingRule());
           out.writeInt(count);
                     // serialize general path
           it = textShape.getPathIterator(null);
           while (!it.isDone()) {
                             float []cords = new float[6];
               int type = it.currentSegment(cords);
               out.writeInt(type);
               switch (type) {
                   case PathIterator.SEG_LINETO:
                   case PathIterator.SEG_MOVETO:
                       out.writeFloat(cords[0]);
                       out.writeFloat(cords[1]);
                       break;
                   case PathIterator.SEG_QUADTO:
                       out.writeFloat(cords[0]);
                       out.writeFloat(cords[1]);
                       out.writeFloat(cords[2]);
                       out.writeFloat(cords[3]);
                       break;
                   case PathIterator.SEG_CUBICTO:
                       out.writeFloat(cords[0]);
                       out.writeFloat(cords[1]);
                       out.writeFloat(cords[2]);
                       out.writeFloat(cords[3]);
                       out.writeFloat(cords[4]);
                       out.writeFloat(cords[5]);
                       break;
                   case PathIterator.SEG_CLOSE:
                       break;
                   default:
                       break;
               }
               it.next();
                         }
               } else {
           out.writeBoolean(false);
       }
   }

   private void readObject(ObjectInputStream in) throws IOException {
       content = new LinkedList();
       try {
           in.defaultReadObject();
       } catch (ClassNotFoundException ex) {
           ex.printStackTrace();
       }
       boolean isThere = in.readBoolean();
       if (isThere) {
           // todo read general path
           int rule = in.readInt();
           textShape = new GeneralPath(rule);
           GeneralPath gp = (GeneralPath)textShape;
           int count = in.readInt();
           float x1, y1, x2, y2, x3, y3;
           for (int i = 0; i < count; i++) {
               int type = in.readInt();
               switch (type) {
                   case PathIterator.SEG_MOVETO:
                       x1 = in.readFloat();
                       y1 = in.readFloat();
                       gp.moveTo(x1, y1);
                       break;
                   case PathIterator.SEG_LINETO:
                       x1 = in.readFloat();
                       y1 = in.readFloat();
                       gp.lineTo(x1, y1);
                       break;
                   case PathIterator.SEG_QUADTO:
                       x1 = in.readFloat();
                       y1 = in.readFloat();
                       x2 = in.readFloat();
                       y2 = in.readFloat();
                       gp.quadTo(x1, y1, x2, y2);
                       break;
                   case PathIterator.SEG_CUBICTO:
                       x1 = in.readFloat();
                       y1 = in.readFloat();
                       x2 = in.readFloat();
                       y2 = in.readFloat();
                       x3 = in.readFloat();
                       y3 = in.readFloat();
                       gp.curveTo(x1, y1, x2, y2, x3, y3);
                       break;
                   case PathIterator.SEG_CLOSE:
                       gp.closePath();
                       break;
                   default:
                       break;
               }
           }
       } else {
           textShape = null;
       }          }

